import React, { useEffect, useState } from 'react';
import CardProduct from './CardProduct';
import AllCats from './AllCats';

export default function Catalog() {
    const [catalogue, setCatalogue] = useState([]);
    const [categoryList, setCategoryList] = useState([]);

    useEffect(() => {
        async function fetchData() {
            try {
                const productsResponse = await fetch("https://667a796bbd627f0dcc8f205a.mockapi.io/products");
                const productsData = await productsResponse.json();
                setCatalogue(productsData);

                const categoriesResponse = await fetch("https://667a796bbd627f0dcc8f205a.mockapi.io/categories");
                const categoriesData = await categoriesResponse.json();
                setCategoryList(categoriesData);
            } catch (error) {
                console.error("Error fetching data: ", error);
            }
        }

        fetchData();
    }, []);

    return (
        <div className="catalog" id="catalog">
            <div className="container">
                <div className="catalog__header">
                    <div className="catalog__select-category dropdown show" id="dropdown-category-list">
                        <h3 className="dropdown-btn">
                            Вибрати категорію
                        </h3>
                        {categoryList.map((category) => (
                            <AllCats key={category.id} temp1={category} />
                        ))}
                    </div>
                    <h3 className="catalog__products-summ">
                        Знайдено товарів: <span id="view-count-products">{catalogue.length}</span>
                    </h3>
                </div>
                <div className="catalog__content" id="catalog-products">
                    <h2>goods are downloaded</h2>
                    {catalogue.map((product) => (
                        <CardProduct key={product.id} element={product} />
                    ))}
                </div>
            </div>
        </div>
    );
}
